import { useState } from 'react'

const CaveEntranceOptions = ({caveEncounter}) => {
  let searchObject = caveEncounter.searches[0]
  console.log(searchObject)

  const [searched, setSearched] = useState(false);
  const [traverseDesc, setTraverseDesc] = useState("searchObject.search_desc")

  const traverseRoom = () => {
    if (searched) {
      return (
        <p>You carefully cross the small path.</p>
      )
    } else {
      return (
        <p>You foolhardedly charge forward into the chasm, sending yourself to death</p>
      )
    }
  };

  const searchRoom = () => {
    fetch("/api/searches/2")
        .then(resp => resp.json())
        .then(search => console.log(search))
        .then(() => setSearched(true))
  };

  return (
    <div id="terrain-options">
      <button onClick={() => traverseRoom()}>Traverse</button>
      <br/>
      <button onClick={() => searchRoom()}>Search</button>
    </div>
  );
};

export default CaveEntranceOptions;
